    "import streamlit as st\n",
    "import pickle\n",
    "import numpy as np\n",
    "import os\n",
    "\n",
    "# Determine the directory of the script or fallback to current working directory\n",
    "try:\n",
    "    HERE = os.path.dirname(os.path.abspath(__file__))\n",
    "except NameError:\n",
    "    HERE = os.getcwd()\n",
    "\n",
    "MODEL_FP = os.path.join(HERE, \"classifier.pkl\")\n",
    "\n",
    "with open(MODEL_FP, \"rb\") as model_file:\n",
    "    model = pickle.load(model_file)\n",
    "\n",
    "st.title(\"Breast Cancer Classifier\")\n",
    "st.write(\"Enter the cell nucleus measurements to classify the cancer\")\n",
